model_embedding_paths = {'RegularSpeciesClassification': regular_classification,
                'EratoNet': erato_net,
                'MelpomeneNet': melpomene_net,
                'MimicsNet': mimics_net}

def get_embedding_paths(acuity, model_name):
    base = model_embedding_paths[model_name][acuity]

    splits = {"train": base + "/train_split",
                "val": base + "/val_split",
                "all": base + "/train_val_test_split",
     }

    if model_name == 'RegularSpeciesClassification' or model_name == 'MimicsNet':
        splits['test'] = base + '/test_split'
    else:
        splits['test_erato']  = base + "/test_erato_split"
        splits['test_melpomene'] = base + "/test_melpomene_split"
    
    
    return splits